import React, { useEffect } from "react";
import '../../css/signup/Signup.css';
import { useState } from "react";
import axios from "axios";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUpload } from '@fortawesome/free-solid-svg-icons';

const Signup = () => {
    const [user, setUser] = useState({
        name: "",
        email: "",
        password: "",
        username: "",
        image: ""
    });

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const res = await axios.get("http://localhost:8800/userdata");
                console.log(res);
            } catch(err) {
                console.log(err);
            }
        }
        fetchUserData();
    }, []);

    const handleChange = (e) => {
        setUser((prev) => ({...prev, [e.target.name]: e.target.value}));
    }

    const handleClick = async (e) => {
        e.preventDefault();

        try {
            await axios.post("http://localhost:8800/userdata", user);
        } catch(err) {
            console.log(err);
        }

        window.location.reload();
    }

    return (
        <div className="signup-component">
            {/* <h1>User Signup</h1>
            <input type="text" placeholder="name" name="name" onChange={handleChange}/>
            <input type="email" placeholder="email" name="email" onChange={handleChange}/>
            <input type="password" placeholder="password" name="password" onChange={handleChange}/>
            <input type="text" placeholder="username" name="username" onChange={handleChange}/>
            <button onClick={handleClick}>Signup</button> */}
            <div className="signup-container">
                <div className='signup-title'>
                    <h1>welcome to harmony</h1>
                    <h3>let's get you started!</h3>
                </div>
                <div className="signup-form">
                    <div className="signup-form-name">
                        <label htmlFor='signup-form-name'>enter your full name</label>
                        <input type='text' id='signup-form-name' name="name" onChange={handleChange}/>
                    </div>
                    <div className="signup-form-email">
                        <label htmlFor='signup-form-email'>enter your email</label>
                        <input type='email' id='signup-form-email' name="email" onChange={handleChange}/>
                    </div>
                    <div className="signup-form-password">
                        <label htmlFor='signup-form-password'>enter your password</label>
                        <input type='password' id='signup-form-password' name="password" onChange={handleChange}/>
                    </div>
                    <div className="signup-form-username">
                        <label htmlFor='signup-form-username'>choose a username for yourself</label>
                        <input type='text' id='signup-form-username' name="username" onChange={handleChange}/>
                    </div>
                    <div className="signup-form-image">
                        <label htmlFor='signup-form-image' style={{width: '100px', marginLeft: 'auto', marginRight: 'auto'}}>
                                {profileImage ? (<img src={profileImage} alt='Profile Image' className='signup-form-image-input-icon signup-form-input-image' />) : (<FontAwesomeIcon icon={faUpload} className='signup-form-image-input-icon signup-form-input-icon' />)}
                            </label>
                        <input type='file' id='signup-form-image' name="image" onChange={handleChange, handleImageInput} accept="image/png, image/jpeg, image/jpg"/>
                    </div>
                    <div className="signup-form-button">
                        <button type="submit" onClick={handleClick}>sign up</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Signup;
